package 문자열;

public class 수박수박수박수 {

	public static String main(String[] args) {
		// TODO Auto-generated method stub
		String answer = "";
		int n = 0;

		for (int i = 0; i < n; i++) {
			if (i % 2 == 0) {
				answer = answer + "수";
			} else if (i % 2 == 1) {
				answer = answer + "박";
			}

		}
		return answer;
	
	}
}