package 연산자;

public class 산술연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x =20;
		int y =10;
		
		
		//컨트롤 + 알트 + 화살표 아래
		System.out.println("더한값은"+(x+y));
		System.out.println("뺀값은"+(x-y));
		System.out.println("곱한값은"+(x*y));
		System.out.println("나눈값은"+(x/y));
		System.out.println("나눈값은"+(x%y));
	}

}
