package 복습;

public class 자바연산시주의점 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x =20;
		int y =30;
		
		System.out.println(x / y);
		
		int x2 =20;
		int y2 =30;
		
		System.out.println(x2 / y2);
		
		System.out.println(x / (double)y);
		
	}

}
