package 조건문;

public class IF문 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 0;

		if (x == 0) {
			System.out.println("내가 실행되고있어!!!");
		} else {
			System.out.println("나는 조건이 맞지 않을 때 실행 돼");
		}
		if (x == 1) {
			System.out.println("내가 실행될까");
		}
	}
}