package 복습;

public class 배열기본확인문제 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] tour = {"파리","일본","스위스","태국","싱가폴"};
		for (int i = 0; i < tour.length; i++) {
			System.out.println(i+":"+tour);
		}
		
		
		
		
		
		char[] color = {'r','y','o','g','b'};
		for (char i = 0; i < color.length; i++) {
			System.out.println(i+":"+color);
		}
		
		
		
		
		
		
		double[] star = {180,170,176,173,175};
		for (double i = 0; i < star.length; i++) {
			System.out.println(i+":"+star);
		}
		
		
		
	}

}
